inp = ['cde',
       'abc']
inp = ['',
       '']
def makeAnagram(s1, s2):
    ln1 = len(s1)
    ln2 = len(s2)
    ar1 = list(s1)
    ar2 = list(s2)
    counter = 0
    if ln1<=ln2:
        for i in range(ln1):
            if ar1[i] not in ar2:
                counter += 1
            else:
                ar2.remove(ar1[i])
    else:
        for i in range(ln2):
            if ar2[i] not in ar1:
                counter += 1
            else:
                ar1.remove(ar2[i])
    return abs(ln1-ln2)+counter*2

print(makeAnagram(inp[0], inp[1]))

